(869, 302, 'career_main_new', 'a:1:{i:0;a:8:{s:5:"title";s:19:"PROGRAMA DE TRAINEE";s:8:"subtitle";s:17:"Somente no Brasil";s:16:"background-image";s:3:"143";s:4:"text";s:1506:"<p>O <span class="text_orange">programa de trainee</span> da Avenue Code é uma ótima oportunidade para profissionais recém-graduados para desenvolver conhecimento em UI ou QA através de experiências de trabalho práticas. </br> \r\nOs novos membros da família Avenue Code serão pareados com desenvolvedores experientes, que os guiarão em um curso de três meses sobre tecnologia e boas práticas.</p>\r\n\r\n<p>Os trainees ganharão experiência em:</p>\r\n<p class="text_orange text_ident"> - Elaboração e implementação de projetos internos na Avenue Code.</br>\r\n- Desenvolvimento e entrega de apresentações sobre tecnologia em nossas reuniões de treinamento.</br>\r\n- Construção e sintonia de protótipos usando as novas habilidades e conhecimentos adquiridos.</br>\r\n- Participação online nas comunidades de tecnologia.</br>\r\n- Leitura de artigos e pesquisa em sites de tecnologia para encontrar informação.</br></p>\r\n\r\n<p>Atualmente possuímos vagas disponíveis em:  <strong>QA e UI</strong></br>\r\nInteressado? Envie seu currículo! Entre em contato pelo e-mail: <a href="http://www.jobs.net/jobs/avenuecode/pt-br/">www.jobs.net/jobs/avenuecode/</a></p>\r\n\r\n<p>Requisitos do Programa: </p>\r\n<p class="text_orange text_ident">- Conhecimento em metodologias Agile</br>\r\n- Paixão por testes ou desenvolvimento de interfaces</br>\r\n- Formação em Ciência da Computação, Sistemas de Informação, Engenharia de Software ou áreas correlatas.  </br>\r\n- No máximo 6 meses de graduado.</br></p>";s:7:"image-1";s:3:"144";s:7:"image-2";s:3:"145";s:7:"image-3";s:3:"146";s:7:"image-4";s:3:"147";}}'),
(870, 302, 'careers_working_at_ac', 'a:18:{i:0;a:4:{s:4:"name";s:0:"";s:5:"image";s:3:"149";s:3:"tag";s:15:"perks, acfamily";s:11:"description";s:17:"Avenue Code Swags";}i:1;a:4:{s:4:"name";s:0:"";s:5:"image";s:3:"150";s:3:"tag";s:8:"acfamily";s:11:"description";s:17:"Latest Technology";}i:2;a:4:{s:4:"name";s:0:"";s:5:"image";s:3:"151";s:3:"tag";s:27:"activities, perks, acfamily";s:11:"description";s:30:"Tasty Thursday (Company Lunch)";}i:3;a:4:{s:4:"name";s:0:"";s:5:"image";s:3:"152";s:3:"tag";s:16:"acfamily, brazil";s:11:"description";s:22:"2014 Brazil World Cup!";}i:4;a:4:{s:4:"name";s:0:"";s:5:"image";s:3:"153";s:3:"tag";s:18:"events, activities";s:11:"description";s:20:"Company Hosted Event";}i:5;a:3:{s:5:"image";s:3:"154";s:3:"tag";s:27:"activities, perks, acfamily";s:11:"description";s:15:"Holiday Parties";}i:6;a:3:{s:5:"image";s:3:"155";s:3:"tag";s:28:"activities, acfamily, brazil";s:11:"description";s:21:"World Cup Decoration ";}i:7;a:3:{s:5:"image";s:3:"156";s:3:"tag";s:14:"events, brazil";s:11:"description";s:37:"Front-In Belo Horizonte event, Brazil";}i:8;a:3:{s:5:"image";s:3:"157";s:3:"tag";s:35:"events, activities, perks, acfamily";s:11:"description";s:21:"Quarterly Team Social";}i:9;a:3:{s:5:"image";s:3:"158";s:3:"tag";s:20:"activities, acfamily";s:11:"description";s:21:"Holiday Gift Exchange";}i:10;a:3:{s:5:"image";s:3:"159";s:3:"tag";s:20:"activities, acfamily";s:11:"description";s:17:"Celebrating wins!";}i:11;a:3:{s:5:"image";s:3:"160";s:3:"tag";s:28:"activities, acfamily, brazil";s:11:"description";s:21:"Avenue Code Hackathon";}i:12;a:3:{s:5:"image";s:3:"161";s:3:"tag";s:13:"perks, brazil";s:11:"description";s:11:"Guitar Hero";}i:13;a:3:{s:5:"image";s:3:"162";s:3:"tag";s:28:"activities, acfamily, brazil";s:11:"description";s:9:"Halloween";}i:14;a:3:{s:5:"image";s:3:"163";s:3:"tag";s:24:"events, acfamily, brazil";s:11:"description";s:28:"Three Year Anniversary Party";}i:15;a:3:{s:5:"image";s:3:"164";s:3:"tag";s:17:"activities, perks";s:11:"description";s:14:"Meet Up Events";}i:16;a:3:{s:5:"image";s:3:"165";s:3:"tag";s:28:"activities, acfamily, brazil";s:11:"description";s:14:"Red-Sox Friday";}i:17;a:3:{s:5:"image";s:3:"166";s:3:"tag";s:28:"activities, acfamily, brazil";s:11:"description";s:12:"Festa Junina";}}'),
